import { setadd , setaddall } from '../util' ;

import EW from './EW';

/**
 * Computes FIRST table for grammar nonterminals.
 *
 * @param grammar
 * @returns {Array}
 */
export default function _first ( grammar ) {

	const FIRST = list( map( _ => new Set() , grammar ) ) ;

	let updated = true;

	while (updated) {

		updated = false;

		for (const [i, rules] of enumerate(grammar)) {

			for (const rule of rules) {

				let read = true;

				for ( const x of rule ) {

					if (!read) break; read = false;

					if ( typeof x === 'string' ) updated |= setadd( FIRST[i], x);
					else {
						updated |= setaddall(FIRST[i], filter(y => y !== EW, FIRST[x]));
						read |= any(map(y => y === EW, FIRST[x]));
					}

				}

				if (read) updated |= setadd(FIRST[i], EW);
			}

		}

	}

	return FIRST;

}

import EOF from './EOF' ;

/**
 * Table-driven predictive parsing.
 *
 * @param table
 * @param rule
 * @param stream
 * @returns {Array}
 */
export default function _parse ( grammar , table , rule , stream , nonterminal , productionid ) {

	const children = [];

	for (const x of rule) {

		const lookahead = stream.read();

		const t = lookahead === stream.eof ? EOF : lookahead ;

		if (typeof x === 'string') {
			if (t === x) { children.push(x); continue; }
			else throw new Error(`Syntax error at token '${t}', expected '${x}'.`) ;
		}

		stream.unread(t);

		const next = table[x].get(t) ;

		if ( next === undefined ) throw new Error(`Syntax error at token '${t}', expected one of '${[...table[x].keys()]}'.`) ;

		else children.push(_parse(grammar, table, grammar[x][next], stream , x , next));

	}

	return {
		nonterminal ,
		'production' : {
			'id' : productionid ,
			rule ,
		} ,
		children
	} ;

}

import EW from './EW' ;

export default function alphabet ( grammar ) {

	const abc = new Set();

	for (const unit of grammar) {
		for (const rule of unit) {
			if (rule.length === 0) {
				abc.add(EW);
				continue;
			}
			for (const part of rule) {
				if (typeof part === 'string') abc.add(part);
			}
		}
	}

	return abc;

}

