export default class Grammar {

	constructor ( { root , start , eof , productions } ) {
		this.root = ''+root ;
		this.start = ''+start ;
		this.eof = eof ;
		this.productions = expandobject( productions ) ;
		this._alphabet = null ;
	}

	alphabet ( ) {
		if ( this._alphabet === null ) this._alphabet = alphabet(this.productions);
		return this._alphabet;
	}

}

function _makenode ( typ , arg ) {
	switch ( typ ) {
		case '&' :
			return { "type" : "node" , "nonterminal" : arg } ;
		case '=' :
			return { "type" : "leaf" , "terminal" : arg } ;
		default :
			throw new Error(`${typ} is not a valid type`) ;
	}
}

export default function* _expandproduction ( production ) {
	for ( const part of production ) {
		if ( typeof part === 'string' ) yield _makenode( part[0] , part.substr(1) ) ;
		else if ( typeof part === 'number' ) yield _makenode( '&' , ''+part ) ;
		else yield part ;
	}
}

import EW from './EW' ;

export default function alphabet ( productions ) {

	const abc = new Set();

	for (const unit of productions.values()) {
		for (const rule of unit.values()) {
			if (rule.length === 0) abc.add(EW);
			else for ( const part of rule ) if (part.type === 'leaf') abc.add( part.terminal ) ;
		}
	}

	return abc;

}

import { map , list } from '@aureooms/js-itertools' ;

import _expandproduction from './_expandproduction' ;

function* _expandproductions ( productions ) {
	for ( const key in productions ) yield [ key , list( _expandproduction( productions[key] ) ) ] ;
}

function* _expandobject ( object ) {
	for ( const nonterminal in object ) yield [ nonterminal , new Map( _expandproductions( object[nonterminal] ) ) ] ;
}

export default function expandobject ( object ) {
	return new Map( _expandobject( object ) ) ;
}

