export default function materialize ( tree ) {
	if ( tree.type === 'leaf' ) return tree ;
	else return {
		'type' : 'node' ,
		'nonterminal' : tree.nonterminal ,
		'production' : tree.production ,
		'children' : list(map(materialize, tree.children)) ,
	} ;
}

